    private void scrPnBudgetModularFocusGained(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_scrPnBudgetModularFocusGained
        if(tblBudgetModular.getRowCount() > 0){
            tblBudgetModular.setRowSelectionInterval(0,0);
            tblBudgetModular.setColumnSelectionInterval(1,1);
            tblBudgetModular.scrollRectToVisible(
            tblBudgetModular.getCellRect(0 ,1, true));
            
            if(tblBudgetModular.getEditorComponent()!=null){
                tblBudgetModular.editCellAt(0, 1);
                tblBudgetModular.getEditorComponent().requestFocusInWindow();
            }
        }
    }//GEN-LAST:event_scrPnBudgetModularFocusGained
    
    
