//Added for modular budget enhancement for table traversing cell end 1
    private void scrPnIndirectCostsFocusGained(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_scrPnIndirectCostsFocusGained
       if(tblIndirectCosts.getRowCount() > 0){
            tblIndirectCosts.setRowSelectionInterval(0,0);
            tblIndirectCosts.setColumnSelectionInterval(1,1);
            tblIndirectCosts.scrollRectToVisible(
            tblIndirectCosts.getCellRect(0 ,1, true));
            tblIndirectCosts.editCellAt(0, 1);
            if(tblIndirectCosts.getEditorComponent()!=null){
                tblIndirectCosts.getEditorComponent().requestFocusInWindow();
            }
        }
    }//GEN-LAST:event_scrPnIndirectCostsFocusGained
    
    
