    private void scrPnFocusGained(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_scrPnFocusGained
        if(tblArgument.getRowCount() > 0){
            tblArgument.setRowSelectionInterval(0,0);
            tblArgument.setColumnSelectionInterval(1,1);
            tblArgument.scrollRectToVisible(tblArgument.getCellRect(0 ,1, true));
           
                tblArgument.editCellAt(0, 1);
                if(tblArgument.getEditorComponent() != null){
                    tblArgument.getEditorComponent().requestFocusInWindow();
                }
        }
    }//GEN-LAST:event_scrPnFocusGained
    
    
